const updatedItems = items.map((item) => {
  let coupon;
  switch (item?.json?.role) {
    case "Student":
      coupon = "STUDENT30";
      break;
    case "Faculty":
      coupon = "FAC20";
      break;
    case "Industry Professional":
      coupon = "IT10";
      break;
    case "Entrepreneur":
      coupon = "E05";
      break;
    case "Freelancer":
      coupon = "FREE20";
      break;
    case "Other":
    default:
      coupon = "PY05";
      break;
  }
  item.json.couponcode_given = coupon;
  return item;
});
return updatedItems;

const items = $input.all();

const updatedItems = items.map((item) => {
  const coupon = item.json.couponcode_given || "";

  // Extract numbers from coupon string (ex: STUDENT30 -> 30 , PY05 -> 05)
  const new_discount_percentage = Number(coupon.replace(/[^0-9]/g, "")) || 0;
  const regFee = Number($('Coupon Validation').first().json.body.registrationFee);

  // Calculate new discount
  const new_discount_amt = (regFee * new_discount_percentage) / 100;

  // New payable amount
  const new_payable_amt = regFee - new_discount_amt;

  // Return only these values - you will store them later in DB
  return {
    json: {
      new_discount_percentage,
      new_discount_amt,
      new_payable_amt
    }
  };
});

return updatedItems;

